def loadInput():
    lines = readInput("input_6.txt")
    area = {}
    start = None
    for y in range(len(lines)):
        for x in range(len(lines[0])):
            if lines[y][x] == ".":
                area[complex(x, y)] = 0
            elif lines[y][x] == "^":
                area[complex(x, y)] = 0
                start = complex(x, y)
            elif lines[y][x] == "#":
                area[complex(x, y)] = 1
    return  area, start

movements = {0: complex(0, -1), 1: complex(1, 0),
             2: complex(0, 1), 3: complex(-1, 0)}

def part1(area, start):
    face = 0
    pos = start
    distincts = set([pos])
    try: 
        while True:
            new_pos = pos + movements[face]
            if area[new_pos] == 1:
                face = (face+1)%4
            else:
                pos = new_pos
                distincts.add((pos))
    except:
        pass
    print (f"ðŸŽ„ Part 1: {len(distincts)}")
    return distincts

def part2(area, start, path):
    blocks = []
    path = list(path)
    path.remove(start)
    for p in path:
        if p in blocks:
            continue
        area[p] = 1
        face = 0
        pos = start
        turns = set([])
        isloop = False
        try:
            while True:
                new_pos = pos + movements[face]
                if area[new_pos] == 1:
                    face = (face+1)%4
                    if (pos, face) in turns:
                        isloop = True
                        break
                    turns.add((pos, face))
                else:
                    pos = new_pos
        except:
            pass
        if isloop:
            blocks.append(p)
        area[p] = 0

    print (f"ðŸŽ„ðŸŽ… Part 2: {len(blocks)}")

